const query = new URLSearchParams({
  session_token: 'YOUR_API_KEY_HERE'
}).toString();
const resp = await fetch(
'https://www.setster.com/api/v2/appointment?' + query,
    {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        data: {
          company_id: 3123,
          client_email: 'user@example.com',
          client_name: 'Test Client',
          client_phone: '12345678',
          client_address: 'string',
          employee_id: 12412,
          location_id: 54232,
          service_id: 453,
          start_date: '2019-09-01 12:00',
          duration: 15,
          note: 'string',
          status: 0,
          subservices: 'string',
          paid: false,
          price: 0,                
          ews_id: '',
          custom_fields: {
            '1': [
              '(01512) 793246',
              'Work Phone'
            ],
            '2': [
              [
                '201 S. Division St.',
                '500',
                'Ann Arbor',
                'MI',
                '48104'
              ],
              'Full Address'
            ],
            '3': [
              '123ABC',
              'Coupon Code',
              'h'
            ]
          },
          timezone_id: 553
        }
      })
    }
  );
  const data = await resp.json();
  console.log(data);

